# Text_UI: Simple text user interface
#
sub Text_UI {  
    my ($file) = @_;
    my $name, $default, $value, $widget, $args;

  AGAIN:

    print "\n\n    "; for (my $i=0; $i<=9+length($RCtitle{$file}); $i++) { printf '-'}; printf "\n"; 
    print     "      Editing $RCtitle{$file}\n";
    print "   "; for (my $i=0; $i<=9+length($RCtitle{$file}); $i++) { printf '-'}; print "\n\n"; 

for ( my $i=0; $i < $nWI{$file}; $i++ ) {

    $name    = $UI[$i]{'name'}{$file};
    $default = $UI[$i]{'value'}{$file};
    $widget  = $UI[$i]{'widget'}{$file};
    $args    = $UI[$i]{'args'}{$file};

    $value = $default;

###    for ( $widget ) {
    $_ = $widget;

SWITCH: {

    /^\&Label/      && do {
                             print "\n$args\n";
                             last SWITCH;
                       };

    /^\&Str/        && do {
                             $value = $default;
                             print "  $args [$default] ";
                             chomp($ans = <STDIN> ); 
                             $ans and $value = $ans;
                             print "";  # cosmetic
                             last SWITCH;
                       };

    /^\&Dir/        && do {
                             $default =~ s/[ ]*//g; 
                             $value = $default;
                             print "  $args [$default] ";
                             chomp($ans = <STDIN> ); 
                             $ans and $value = $ans;
                             print "";  # cosmetic
                              last SWITCH;
                      };

    /^\&File/        && do {
                             $default =~ s/[ ]*//g; 
                             $value = $default;
                             print "  $args [$default] ";
                             chomp($ans = <STDIN> ); 
                             $ans and $value = $ans;
                             print "";  # cosmetic
                             last SWITCH;
                      };

    /\&YesNo/      && do {
                             $default =~ s/[ ]*//g; 
                             $value = $default;
                             print "  $args [$default] ";
                             chomp($ans = <STDIN> ); 
                             $ans and $value = $ans;
                             if ( lc $value eq "y"   ||
                                  lc $value eq "n"   ||
                                  lc $value eq "yes" ||
                                  lc $value eq "no"  ) {
                                  print "";  # cosmetic
                                  last SWITCH;
                             } else {
                               print STDERR "$0: enter either Y or N -------\n";
                               goto SWITCH;
                             }

                       };
		       
    /^\&CheckBox/      && do {
                             $default =~ s/[ ]*//g; 
                             $value = $default;
                             print "  $args [$default] ";
                             chomp($ans = <STDIN> ); 
                             $ans and $value = $ans;
                             if ( lc $value eq "1"   ||
                                  lc $value eq "0"   ||
                                  lc $value eq "off" ||
                                  lc $value eq "on"  ) {
                                  print "";  # cosmetic
                                  last SWITCH;
                             } else {
                               print STDERR "$0: enter either Y or N ++++++++\n";
                               goto SWITCH;
                             }

                       };
		       

    } # SWITCH

### }

    $UI[$i]{'value'}{$file} = $value;  # updated resource file

  } # for each UI request

    print "\n";

#   Update resources
#   ----------------
    Update_RC($file);
    Check_RC($file);

#   Write updated resources to file
#   -------------------------------
    if ($nError{$file}>0) {
      print "--> Found $nError{$file} errors in <$file>\n";
      for ( $i=0; $i < $nError{$file}; $i++ ) {
        my $err = $i+1; 
        print "    $err) " . $Error[$i]{$file} . "\n"; 
      }
      goto AGAIN;
      } else {
        Write_RC($file, $outfn);  
      }

}

#.........................................................................
#
# No_UI:  No user interaction
#

sub No_UI {
  my $file = shift;

#   Update resources
#   ----------------
    Update_RC($file);
    Check_RC($file);

#   Write updated resources to file
#   -------------------------------
    Write_RC($file, $outfn);  
    print "--> Found $nError{$file} errors in <$file>\n" if $nError{$file}; 
    for ( $i=0; $i < $nError{$file}; $i++ ) {
      my $err = $i+1; 
      print "    $err) " . $Error[$i]{$file} . "\n"; 
    }

  }

